/*global tinymce, jQuery */

(function (tinymce, $) {
    'use strict';

    var AutoComplete = function (ed, options) {
        this.editor = ed;

        this.options = $.extend({}, {
            source: [],
            delay: 500,
            queryBy: 'name',
            item: '',
            items: 10
        }, options);

        this.matcher = this.options.matcher || this.matcher;
        this.renderDropdown = this.options.renderDropdown || this.renderDropdown;
        this.render = this.options.render || this.render;
        this.insert = this.options.insert || this.insert;
        this.highlighter = this.options.highlighter || this.highlighter;
        this.start = 0;
        this.query = '';
        this.hasFocus = true;

        this.renderInput();

        this.bindEvents();
        //$(this.editor.getBody()).find('#autocomplete-searchtext').text(this.options.item);
        if (this.options.item.length > 0) this.lookup();
    };

    AutoComplete.prototype = {

        constructor: AutoComplete,

        renderInput: function () {
            var rawHtml = '<span class="autocompleter">' +
                '<span id="autocomplete-delimiter">' + (this.options.item.length > 0 ? this.options.item[0] : this.options.delimiter) + '</span>' +
                '<span id="autocomplete-searchtext"><span class="dummy">\uFEFF' + (this.options.item.length > 1 ? this.options.item.substring(1) : "") + '</span></span>' +
                '</span>';

            if (this.options.item.length > 0) {
                var selection = this.editor.selection;
                var editorRange = selection.getRng();

                var node = editorRange.commonAncestorContainer; // relative node to the selection
                var range = tinymce.DOM.createRng(); // create a new range object for the deletion
                range.selectNodeContents(node);
                range.setStart(node, editorRange.endOffset - this.options.item.length ); // current caret pos - 3 
                range.setEnd(node, editorRange.endOffset); // current caret pos
                range.deleteContents();
            }
            this.editor.execCommand('mceInsertContent', false, rawHtml);

            this.editor.focus();
            this.editor.selection.select(this.editor.selection.dom.select('span#autocomplete-searchtext span')[0]);
            this.editor.selection.collapse(0);

        },

        bindEvents: function () {
            this.editor.on('keyup', this.editorKeyUpProxy = $.proxy(this.rteKeyUp, this));
            this.editor.on('keydown', this.editorKeyDownProxy = $.proxy(this.rteKeyDown, this), true);
            this.editor.on('click', this.editorClickProxy = $.proxy(this.rteClicked, this));

            $('body').on('click', this.bodyClickProxy = $.proxy(this.rteLostFocus, this));

            $(this.editor.getWin()).on('scroll', this.rteScroll = $.proxy(function () { this.cleanUp(true); }, this));
        },

        unbindEvents: function () {
            this.editor.off('keyup', this.editorKeyUpProxy);
            this.editor.off('keydown', this.editorKeyDownProxy);
            this.editor.off('click', this.editorClickProxy);

            $('body').off('click', this.bodyClickProxy);

            $(this.editor.getWin()).off('scroll', this.rteScroll);
        },

        rteKeyUp: function (e) {
            switch (e.which || e.keyCode) {
                //DOWN ARROW
                case 40:
                //UP ARROW
                case 38:
                //SHIFT
                case 16:
                //CTRL
                case 17:
                //ALT
                case 18:
                    break;

                //BACKSPACE
                case 8:
                    if (this.query === '') {
                        this.cleanUp(true);
                    } else {
                        this.lookup();
                    }
                    break;

                //TAB
                case 9:
                //ENTER
                case 13:
                    var item = (this.$dropdown !== undefined) ? this.$dropdown.find('li.active') : [];
                    if (item.length) {
                        var data = this.options.queryBy ? "data-" + this.options.queryBy : "data";
                        this.select(item.attr(data));
                        this.cleanUp(false);
                    } else {
                        this.cleanUp(true);
                    }
                    break;

                //ESC
                case 27:
                    this.cleanUp(true);
                    break;

                default:
                    this.lookup();
            }
        },

        rteKeyDown: function (e) {
            switch (e.which || e.keyCode) {
                //TAB
                case 9:
                //ENTER
                case 13:
                //ESC
                case 27:
                    e.preventDefault();
                    break;

                //UP ARROW
                case 38:
                    e.preventDefault();
                    if (this.$dropdown !== undefined) {
                        this.highlightPreviousResult();
                    }
                    break;
                //DOWN ARROW
                case 40:
                    e.preventDefault();
                    if (this.$dropdown !== undefined) {
                        this.highlightNextResult();
                    }
                    break;
            }

            e.stopPropagation();
        },

        rteClicked: function (e) {
            var $target = $(e.target);

            if (this.hasFocus && $target.parent().attr('id') !== 'autocomplete-searchtext') {
                this.cleanUp(true);
            }
        },

        rteLostFocus: function () {
            if (this.hasFocus) {
                this.cleanUp(true);
            }
        },

        lookup: function () {
            this.query = $.trim($(this.editor.getBody()).find('#autocomplete-searchtext').text()).replace('\ufeff', '');

            if (this.$dropdown === undefined) {
                this.show();
            }

            clearTimeout(this.searchTimeout);
            this.searchTimeout = setTimeout($.proxy(function () {
                // Added delimiter parameter as last argument for backwards compatibility.
                var items = $.isFunction(this.options.source) ? this.options.source(this.query, $.proxy(this.process, this), this.options.delimiter) : this.options.source;
                if (items) {
                    this.process(items);
                }
            }, this), this.options.delay);
        },

        matcher: function (item) {
            return this.options.queryBy ? ~item[this.options.queryBy].toLowerCase().indexOf(this.query.toLowerCase()) : ~item.toLowerCase().indexOf(this.query.toLowerCase());;
            //return this.options.queryBy ? item[this.options.queryBy].toLowerCase().indexOf(this.query.toLowerCase()) == 0 : item.toLowerCase().indexOf(this.query.toLowerCase()) == 0;
        },

        sorter: function (items) {
            var beginswith = [],
                caseSensitive = [],
                caseInsensitive = [],
                item;

            while ((item = items.shift()) !== undefined) {
                var index = this.options.queryBy ? item[this.options.queryBy].toLowerCase().indexOf(this.query.toLowerCase()) : item.toLowerCase().indexOf(this.query.toLowerCase());
                var match = this.options.queryBy ? ~item[this.options.queryBy].indexOf(this.query) : item.indexOf(this.query);
                if (!index) {
                    beginswith.push(item);
                } else if (match) {
                    caseSensitive.push(item);
                } else {
                    caseInsensitive.push(item);
                }
            }

            return beginswith.concat(caseSensitive, caseInsensitive);
        },

        highlighter: function (text) {
            return text.replace(new RegExp('(' + this.query.replace(/([.?*+^$[\]\\(){}|-])/g, '\\$1') + ')', 'ig'), function ($1, match) {
                return '<strong>' + match + '</strong>';
            });
        },

        show: function () {
            var offset = this.editor.inline ? this.offsetInline() : this.offset();

            this.$dropdown = $(this.renderDropdown())
                .css({ 'top': offset.top, 'left': offset.left });

            $('body').append(this.$dropdown);

            this.$dropdown.on('click', $.proxy(this.autoCompleteClick, this));
        },

        process: function (data) {
            if (!this.hasFocus) {
                return;
            }

            var _this = this,
                result = [],
                items = $.grep(data, function (item) {
                    return _this.matcher(item);
                });

            items = _this.sorter(items);

            items = items.slice(0, this.options.items);
            var queryBy = this.options.queryBy;

            $.each(items, function (i, item) {
                var $element = $(_this.render(item));

                $element.html($element.html().replace($element.text(), _this.highlighter($element.text())));

                if (queryBy)
                    $.each(items[i], function (key, val) {
                        $element.attr('data-' + key, val);
                    });
                else
                    $element.attr('data' , items[i]);

                result.push($element[0].outerHTML);
            });

            if (result.length) {
                this.$dropdown.html(result.join('')).show();
                this.highlightNextResult();
            } else {
                this.$dropdown.hide();
            }
        },

        renderDropdown: function () {
            return '<ul class="rte-autocomplete dropdown-menu"><li class="loading"></li></ul>';
        },

        render: function (item) {
            var text = this.options.queryBy ? item[this.options.queryBy] : item;
            return '<li>' +
                '<a href="javascript:;"><span>' + text + '</span></a>' +
                '</li>';
        },

        autoCompleteClick: function (e) {
            var data = this.options.queryBy ? "data-" + this.options.queryBy : "data";
            var item = $(e.target).closest('li').attr(data);
            if (!$.isEmptyObject(item)) {
                this.select(item);
                this.cleanUp(false);
            }
            e.stopPropagation();
            e.preventDefault();
        },

        highlightPreviousResult: function () {
            var currentIndex = (this.$dropdown.find('li.active').length > 0 ? this.$dropdown.find('li.active').index() : this.$dropdown.find('li').length);
            var index = (currentIndex === 0) ? this.$dropdown.find('li').length - 1 : --currentIndex;

            this.$dropdown.find('li').removeClass('active').eq(index).addClass('active');
        },

        highlightNextResult: function () {
            var currentIndex = (this.$dropdown.find('li.active').length > 0 ? this.$dropdown.find('li.active').index() : 0);
            var index = (currentIndex === this.$dropdown.find('li').length - 1) ? 0 : ++currentIndex;

            this.$dropdown.find('li').removeClass('active').eq(index).addClass('active');
        },

        select: function (item) {
            this.editor.focus();
            var selection = this.editor.dom.select('span.autocompleter')[0];
            this.editor.dom.remove(selection);
            this.editor.execCommand('mceInsertContent', false, this.insert(item));
        },

        insert: function (item) {
            var text = /*this.options.queryBy ? item[this.options.queryBy] :*/ item;
            return text;
        },

        cleanUp: function (rollback) {
            this.unbindEvents();
            this.hasFocus = false;

            if (this.$dropdown !== undefined) {
                this.$dropdown.remove();
                delete this.$dropdown;
            }

            if (rollback) {
                var text = this.query,
                    $selection = $(this.editor.dom.select('span.autocompleter')),
                    replacement = $('<p>' + this.options.delimiter + text + '</p>')[0].firstChild,
                    focus = $(this.editor.selection.getNode()).offset().top === ($selection.offset().top + (($selection.outerHeight() - $selection.height()) / 2));

                this.editor.dom.replace(replacement, $selection[0]);

                if (focus) {
                    this.editor.selection.select(replacement);
                    this.editor.selection.collapse();
                }
            }
        },

        offset: function () {
            var rtePosition = $(this.editor.getContainer()).offset(),
                contentAreaPosition = $(this.editor.getContentAreaContainer()).position(),
                nodePosition = $(this.editor.dom.select('span.autocompleter')).position();

            return {
                top: rtePosition.top + contentAreaPosition.top + nodePosition.top + $(this.editor.selection.getNode()).innerHeight() - $(this.editor.getDoc()).scrollTop() + 5,
                left: rtePosition.left + contentAreaPosition.left + nodePosition.left
            };
        },

        offsetInline: function () {
            var nodePosition = $(this.editor.dom.select('span.autocompleter')).offset();

            return {
                top: nodePosition.top + $(this.editor.selection.getNode()).innerHeight() + 5,
                left: nodePosition.left
            };
        }

    };

    tinymce.init({
        selector: 'mentions',
        pluginId: 'mention'
    });

    tinymce.PluginManager.add('mention', (editor, url) => {

            var autoComplete,
            autoCompleteData = editor.getParam('mentions');

            // If the delimiter is undefined set default value to ['@'].
            // If the delimiter is a string value convert it to an array. (backwards compatibility)
            autoCompleteData.delimiter = (autoCompleteData.delimiter !== undefined) ? !$.isArray(autoCompleteData.delimiter) ? [autoCompleteData.delimiter] : autoCompleteData.delimiter : ['@'];

            function prevCharIsSpace() {
                var start = editor.selection.getRng(true).startOffset,
                text = editor.selection.getRng(true).startContainer.data || '',
                charachter = text.substr(start - 1, 1);

                return(!!$.trim(charachter).length) ? false : true;
            }
        function getStart() {
            var start = editor.selection.getRng(true).startOffset,
                text = editor.selection.getRng(true).startContainer.data || '',
                charachter = text.substr(start - 1, 1);
            while (start > 0 && $.inArray(charachter, autoCompleteData.delimiter) < 0) {
                start--;
                charachter = text.substr(start - 1, 1);
            }
            return $.inArray(charachter, autoCompleteData.delimiter) >= 0 ? start : -1;
        }
        function getDelimiter(start) {
            var text = editor.selection.getRng(true).startContainer.data || '';
            var charachter = text.substr(start, 1);
            return $.inArray(charachter, autoCompleteData.delimiter);;
        }
        function getText(start) {
            var text = editor.selection.getRng(true).startContainer.data || '';
            return text.substring(start-1, editor.selection.getRng(true).startOffset);
        }
            editor.on('keypress', function (e) {
                var delimiterIndex = $.inArray(String.fromCharCode(e.which || e.keyCode), autoCompleteData.delimiter);
                if (delimiterIndex > -1) {
                    //                    if (delimiterIndex > -1 && prevCharIsSpace()) {
                    if (autoComplete === undefined || (autoComplete.hasFocus !== undefined && !autoComplete.hasFocus)) {
                        autoCompleteData.item = "";
                        e.preventDefault();
                        // Clone options object and set the used delimiter.
                        autoComplete = new AutoComplete(editor, $.extend({}, autoCompleteData, { delimiter: autoCompleteData.delimiter[delimiterIndex] }));
                    }
                } else {
                    if (e.key == " " && e.ctrlKey) {
                        var start = getStart();
                        if (autoComplete === undefined || (autoComplete.hasFocus !== undefined && !autoComplete.hasFocus)) {
                                var delimiterIndex = $.inArray(String.fromCharCode(e.which || e.keyCode), autoCompleteData.delimiter);
                                e.preventDefault();
                                // Clone options object and set the used delimiter.
                            if (start >= 0)
                                autoCompleteData.item = getText(start);
                            else
                                autoCompleteData.item = "";
                            delimiterIndex = getDelimiter(start);
                            if (delimiterIndex < 0) delimiterIndex = 0;
                            autoComplete = new AutoComplete(editor, $.extend({}, autoCompleteData, { delimiter: autoCompleteData.delimiter[delimiterIndex] }));
                        }
                    }
                }
            });

        return {
            name: 'mention',
            author: 'Steven Devooght',
            version: tinymce.majorVersion + '.' + tinymce.minorVersion
        };

    });


    //tinymce.PluginManager.add('mention', tinymce.plugins.Mention);

}(tinymce, jQuery));